pragma solidity ^0.6.0;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./IExerciceSolution.sol";

contract TokenERC20 is ERC20, IExerciceSolution
{
    constructor(address evaluator1) public ERC20("TomToken", "pdE1B"){
        //_mint(msg.sender,767416516000000000000000000);
    }
    address[] private whitelist;
    mapping(address => bool) tier1;
    mapping(address => bool) tier2;
    mapping(address => bool) public isAllowed;
    mapping(address => uint) public level;

    function symbol() public override(ERC20, IExerciceSolution) view returns (string memory){
        return "pdE1B";
    }
    function getToken() external override returns (bool){
        if (this.isCustomerWhiteListed(msg.sender)){
            if (this.customerTierLevel(msg.sender)==2){
                _mint(msg.sender, 200);
                return true;
            }else if (this.customerTierLevel(msg.sender)==1){
                _mint(msg.sender, 100);
                return true;
            }
            return false;
        }
        return false;
    }
    function buyToken() external override payable returns (bool){
        if (this.isCustomerWhiteListed(msg.sender)){
            if (this.customerTierLevel(msg.sender)==2){
                uint256 amount = 20 * msg.value;
                _mint(msg.sender, amount);
                return true;
            }else if (this.customerTierLevel(msg.sender)==1){
                uint256 amount = 10 * msg.value;
                _mint(msg.sender, amount);
                return true;
            }
            return false;
        }
        return false;
    }
    function removeWhitelist(address customerAddress) public  {
        address[] memory tmpWhitelist = whitelist;
        whitelist = new address[](0); // Reset the whitelist

        for (uint i = 0; i < tmpWhitelist.length; i++){
            if(tmpWhitelist[i] != customerAddress){
                whitelist.push(tmpWhitelist[i]);
            }
        }
    }
    function addWhitelist(address customerAddress) public  {
        whitelist.push(customerAddress);
    }
    function isCustomerWhiteListed(address customerAddress) external override returns (bool){
        for (uint i = 0; i < whitelist.length; i++){
            if(whitelist[i] == customerAddress){
                return true;
            }
        }
        return false;

    }
    function customerTierLevel(address customerAddress) external override returns (uint256){
        if(tier2[customerAddress]){
            return 2;
        }
        else{
            if(tier1[customerAddress]){
                return 1;
            }
            else{
                return 0;
            }
        }
    }

}
